mapper.update:
update board_post
set views=views+1
where id=#{param1}

update board_post
set title=#{param1}, content=#{param2}, postdate=to_char(sysdate, 'YYYY-MM-DD')
where id=#{param3}

mapper.select:
select count(*)
from post_heart
where post_id=#{param1}
and heart_id=#{param2}

mapper.update:
update board_post
set heart=heart+1
where id=#{param1}
